import React, { useState, useEffect } from 'react';
import './App.css';

// API Base URL - change this to your Flask server URL
const API_BASE_URL = 'http://localhost:5000/api';

// API service functions
const api = {
  async get(endpoint) {
    const response = await fetch(`${API_BASE_URL}${endpoint}`);
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    return response.json();
  },

  async post(endpoint, data) {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
    }
    return response.json();
  },

  async delete(endpoint) {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: 'DELETE',
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
    }
    return response.json();
  }
};

// Portfolio Summary Component
const PortfolioSummary = ({ metrics }) => {
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2
    }).format(amount);
  };

  const formatPercentage = (percent) => {
    return `${percent >= 0 ? '+' : ''}${percent.toFixed(2)}%`;
  };

  return (
    <div className="summary-cards">
      <div className="summary-card">
        <div className="card-header">
          <span className="card-title">Total Value</span>
          <span className="card-icon">💰</span>
        </div>
        <div className="card-value">{formatCurrency(metrics.total_value || 0)}</div>
      </div>

      <div className="summary-card">
        <div className="card-header">
          <span className="card-title">Total Cost</span>
          <span className="card-icon"></span>
        </div>
        <div className="card-value">{formatCurrency(metrics.total_cost || 0)}</div>
      </div>

      <div className="summary-card">
        <div className="card-header">
          <span className="card-title">Gain/Loss</span>
          <span className="card-icon">{(metrics.total_gain_loss || 0) >= 0 ? '📈' : '📉'}</span>
        </div>
        <div className={`card-value ${(metrics.total_gain_loss || 0) >= 0 ? 'positive' : 'negative'}`}>
          {formatCurrency(metrics.total_gain_loss || 0)}
        </div>
      </div>

      <div className="summary-card">
        <div className="card-header">
          <span className="card-title">Return %</span>
          <span className="card-icon">🎯</span>
        </div>
        <div className={`card-value ${(metrics.gain_loss_percentage || 0) >= 0 ? 'positive' : 'negative'}`}>
          {formatPercentage(metrics.gain_loss_percentage || 0)}
        </div>
      </div>
    </div>
  );
};

// Add Stock Form Component
const AddStockForm = ({ onAddStock, isLoading }) => {
  const [formData, setFormData] = useState({
    ticker: '',
    shares: '',
    buy_price: ''
  });

  const [errors, setErrors] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    const newErrors = {};
    if (!formData.ticker.trim()) newErrors.ticker = 'Ticker is required';
    if (!formData.shares || parseFloat(formData.shares) <= 0) newErrors.shares = 'Valid number of shares required';
    if (!formData.buy_price || parseFloat(formData.buy_price) <= 0) newErrors.buy_price = 'Valid buy price required';

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      try {
        await onAddStock({
          ticker: formData.ticker.trim().toUpperCase(),
          shares: parseFloat(formData.shares),
          buy_price: parseFloat(formData.buy_price)
        });
        setFormData({ ticker: '', shares: '', buy_price: '' });
      } catch (error) {
        console.error('Error adding stock:', error);
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  return (
    <div className="add-stock-form">
      <h3>Add New Stock</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="ticker">Ticker Symbol</label>
            <input
              type="text"
              id="ticker"
              name="ticker"
              value={formData.ticker}
              onChange={handleChange}
              placeholder="e.g., AAPL"
              className={errors.ticker ? 'error' : ''}
              disabled={isLoading}
            />
            {errors.ticker && <span className="error-message">{errors.ticker}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="shares">Shares</label>
            <input
              type="number"
              id="shares"
              name="shares"
              value={formData.shares}
              onChange={handleChange}
              placeholder="10"
              min="0.01"
              step="0.01"
              className={errors.shares ? 'error' : ''}
              disabled={isLoading}
            />
            {errors.shares && <span className="error-message">{errors.shares}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="buy_price">Buy Price</label>
            <input
              type="number"
              id="buy_price"
              name="buy_price"
              value={formData.buy_price}
              onChange={handleChange}
              placeholder="150.00"
              min="0.01"
              step="0.01"
              className={errors.buy_price ? 'error' : ''}
              disabled={isLoading}
            />
            {errors.buy_price && <span className="error-message">{errors.buy_price}</span>}
          </div>

          <button type="submit" className="btn-primary" disabled={isLoading}>
            {isLoading ? 'Adding...' : '+ Add Stock'}
          </button>
        </div>
      </form>
    </div>
  );
};

// Holdings Table Component
const HoldingsTable = ({ holdings, onDeleteHolding, onRefreshPrices, isLoading }) => {
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2
    }).format(amount);
  };

  const formatPercentage = (percent) => {
    return `${percent >= 0 ? '+' : ''}${percent.toFixed(2)}%`;
  };

  const calculateHoldingMetrics = (holding) => {
    const marketValue = holding.shares * holding.current_price;
    const cost = holding.shares * holding.buy_price;
    const gainLoss = marketValue - cost;
    const returnPercentage = (gainLoss / cost) * 100;

    return { marketValue, cost, gainLoss, returnPercentage };
  };

  if (holdings.length === 0) {
    return (
      <div className="holdings-empty">
        <h3>Your Portfolio</h3>
        <p>No stocks in your portfolio yet. Add your first stock above!</p>
      </div>
    );
  }

  return (
    <div className="holdings-table-container">
      <div className="table-header">
        <h3>Current Holdings</h3>
        <button 
          onClick={onRefreshPrices} 
          className="btn-refresh"
          disabled={isLoading}
        >
          {isLoading ? '⟳ Refreshing...' : '🔄 Refresh Prices'}
        </button>
      </div>

      <div className="table-wrapper">
        <table className="holdings-table">
          <thead>
            <tr>
              <th>Ticker</th>
              <th>Sector</th>
              <th>Shares</th>
              <th>Buy Price</th>
              <th>Current Price</th>
              <th>Market Value</th>
              <th>Gain/Loss</th>
              <th>Return %</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {holdings.map((holding) => {
              const { marketValue, gainLoss, returnPercentage } = calculateHoldingMetrics(holding);

              return (
                <tr key={holding.id}>
                  <td className="ticker-cell">
                    <strong>{holding.ticker}</strong>
                  </td>
                  <td>{holding.sector}</td>
                  <td>{holding.shares}</td>
                  <td>{formatCurrency(holding.buy_price)}</td>
                  <td>{formatCurrency(holding.current_price)}</td>
                  <td><strong>{formatCurrency(marketValue)}</strong></td>
                  <td className={gainLoss >= 0 ? 'positive' : 'negative'}>
                    <strong>{formatCurrency(gainLoss)}</strong>
                  </td>
                  <td className={returnPercentage >= 0 ? 'positive' : 'negative'}>
                    <strong>{formatPercentage(returnPercentage)}</strong>
                  </td>
                  <td>
                    <button 
                      onClick={() => onDeleteHolding(holding.id)}
                      className="btn-delete"
                      title="Delete holding"
                    >
                      🗑️
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Sector Breakdown Component
const SectorBreakdown = ({ sectorData }) => {
  if (!sectorData || sectorData.length === 0) {
    return (
      <div className="sector-breakdown">
        <h3>Sector Allocation</h3>
        <p>No data available</p>
      </div>
    );
  }

  return (
    <div className="sector-breakdown">
      <h3>Sector Allocation</h3>
      <div className="sector-list">
        {sectorData.map((sector, index) => (
          <div key={sector.sector} className="sector-item">
            <div className="sector-info">
              <div 
                className="sector-color" 
                style={{ backgroundColor: sector.color }}
              ></div>
              <span className="sector-name">{sector.sector}</span>
            </div>
            <div className="sector-values">
              <span className="sector-percentage">{sector.percentage.toFixed(1)}%</span>
              <span className="sector-value">
                ${sector.value.toLocaleString('en-US', { minimumFractionDigits: 2 })}
              </span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Main App Component
const App = () => {
  const [holdings, setHoldings] = useState([]);
  const [metrics, setMetrics] = useState({});
  const [sectorData, setSectorData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  // Load initial data
  useEffect(() => {
    loadPortfolioData();
    loadSectorData();
  }, []);

  const loadPortfolioData = async () => {
    try {
      setIsLoading(true);
      const data = await api.get('/portfolio');
      setHoldings(data.holdings || []);
      setMetrics(data.metrics || {});
      setError('');
    } catch (err) {
      setError('Failed to load portfolio data. Make sure the Flask server is running.');
      console.error('Error loading portfolio:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const loadSectorData = async () => {
    try {
      const data = await api.get('/sector-breakdown');
      setSectorData(data);
    } catch (err) {
      console.error('Error loading sector data:', err);
    }
  };

  const handleAddStock = async (stockData) => {
    try {
      setIsLoading(true);
      await api.post('/holdings', stockData);
      await loadPortfolioData();
      await loadSectorData();
      setError('');
    } catch (err) {
      setError(err.message || 'Failed to add stock');
      throw err;
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteHolding = async (holdingId) => {
    if (window.confirm('Are you sure you want to delete this holding?')) {
      try {
        setIsLoading(true);
        await api.delete(`/holdings/${holdingId}`);
        await loadPortfolioData();
        await loadSectorData();
        setError('');
      } catch (err) {
        setError(err.message || 'Failed to delete holding');
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleRefreshPrices = async () => {
    try {
      setIsLoading(true);
      const data = await api.post('/refresh-prices', {});
      setHoldings(data.holdings || []);
      setMetrics(data.metrics || {});
      await loadSectorData();
      setError('');
    } catch (err) {
      setError(err.message || 'Failed to refresh prices');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>📊 Stock Portfolio Analyzer</h1>
        <p>Track your investments and analyze performance in real-time</p>
      </header>

      {error && (
        <div className="error-banner">
          <span>⚠️ {error}</span>
          <button onClick={() => setError('')} className="error-close">×</button>
        </div>
      )}

      <main className="app-main">
        <PortfolioSummary metrics={metrics} />

        <AddStockForm onAddStock={handleAddStock} isLoading={isLoading} />

        <div className="content-grid">
          <div className="holdings-section">
            <HoldingsTable 
              holdings={holdings}
              onDeleteHolding={handleDeleteHolding}
              onRefreshPrices={handleRefreshPrices}
              isLoading={isLoading}
            />
          </div>

          <div className="sidebar">
            <SectorBreakdown sectorData={sectorData} />
          </div>
        </div>
      </main>

      <footer className="app-footer">
        <p>Built with React & Flask | Demo using simulated data</p>
        <p><small>💡 In production, integrate with real APIs like Alpha Vantage or Finnhub</small></p>
      </footer>
    </div>
  );
};

export default App;
